
DROP TABLE IF EXISTS "manga";

CREATE TABLE IF NOT EXISTS "manga" (
  "id"       SERIAL PRIMARY KEY  NOT NULL,
  "nom_japonais" VARCHAR(85) NOT NULL,
  "nom_traduit" VARCHAR(82) NOT NULL,
  "category"      VARCHAR(13),
  "type"       VARCHAR(11) NOT NULL,
  "statut"       VARCHAR(16),
  "auteur"       VARCHAR(88) NOT NULL,
  "manga_photo"  VARCHAR(110)
);

